mapper.update:
UPDATE users
SET password = #{password}
WHERE id = #{id}

mapper.insert:
INSERT INTO user_reset_password_token
(user_id, token, create_date, expire_date)
VALUES(#{user_id} #{token}, NOW(6), NOW(6));

mapper.select:
SELECT user_id, token FROM user_reset_password_token
WHERE token = #{token}
AND expire_date  <=   NOW(6)
